class BottomSemiCircle extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    double curveHeight = 40; // fixed curve height

    Path path = Path();
    path.moveTo(0, 0);
    path.quadraticBezierTo(
      size.width / 2, curveHeight,
      size.width, 0,
    );

    // Draw down to full height without affecting curve
    path.lineTo(size.width, size.height);
    path.lineTo(0, size.height);
    path.close();

    return path;
  }

  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => false;
}




class GlassButtonStyled extends StatelessWidget {
  final String text;
  final LinearGradient gradient;
  final bool selected;
  final double fontSize;

  const GlassButtonStyled({
    super.key,
    required this.text,
    required this.gradient,
    required this.selected,
    required this.fontSize,
  });

  @override
  Widget build(BuildContext context) {
    return ClipRRect(
      borderRadius: BorderRadius.circular(30),
      child: BackdropFilter(
        filter: ImageFilter.blur(sigmaX: 10, sigmaY: 10),
        child: AnimatedContainer(
          duration: const Duration(milliseconds: 300),
          padding: EdgeInsets.symmetric(vertical: ResponsiveUtil.height(9)),
          decoration: BoxDecoration(
            gradient: selected ? gradient : null,
            color: !selected ? Colors.transparent : null,
            borderRadius: BorderRadius.circular(30),
            border: Border.all(
              color: selected ? Colors.white70 : Colors.transparent,
              width: selected ? 2 : 0,
            ),
          ),
          alignment: Alignment.center,
          child: Text(
            text,
            style: TextStyle(
              fontFamily: "Montserrat-BoldItalic",
              color: selected ? Colors.white : Colors.black,
              fontWeight: FontWeight.bold,
              fontSize: fontSize,
              shadows: selected
                  ? const [
                Shadow(
                  color: Colors.black38,
                  offset: Offset(1, 1),
                  blurRadius: 2,
                )
              ]
                  : [],
            ),
          ),
        ),
      ),
    );
  }
}




